import React, { Component } from 'react'
import store from '../../redux/store' 

export default class  extends Component {
    // state = {count: 0}
    componentDidMount() {
        // monitoring redux state changes. If change, implement render
        store.subscribe(() => {
            this.setState({})

        })
    }
    increment = () => {
        const {value} = this.selectNumber
        store.dispatch({type: 'increment', data: value*1})
    }

    decrement = () => {
        const {value} = this.selectNumber
        store.dispatch({type: 'decrement', data: value*1})
    }

    incrementIfOdd = () => {
        const {value} = this.selectNumber
        const count = store.getState()
        if (count % 2 !== 0) {
            store.dispatch({type: 'increment', data: value*1})
        }

    }

    incrementAsync = () => {
        const {value} = this.selectNumber
        const {count} = this.state
        setTimeout(() => {
            this.setState({count:count+value*1})
        }, 500)
    }


  render() {
    return (
      <div>
        <h1>total is: {store.getState()}</h1>
        <select ref={c => this.selectNumber = c}>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
        </select>&nbsp;
        <button onClick={this.increment}>+</button>&nbsp;
        <button onClick={this.decrement}>-</button>&nbsp;
        <button onClick={this.incrementIfOdd}>increment if the current sum is odd</button>&nbsp;
        <button onClick={this.incrementAsync}>add asynchronizedly</button>
      </div>
    )
  }
}
